import React, { useState, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Animated,
  TextInput,
  TouchableOpacity,
  Dimensions,
} from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';
import { useTheme } from 'react-native-paper';

const History = () => {
  const theme = useTheme();
  const [isExpanded, setIsExpanded] = useState(false);
  const animatedWidth = useRef(new Animated.Value(50)).current;
  const animatedOpacity = useRef(new Animated.Value(0)).current;
  const inputRef = useRef<TextInput>(null);

  const expandAnimation = () => {
    setIsExpanded(true);
    Animated.parallel([
      Animated.timing(animatedWidth, {
        toValue: Dimensions.get('window').width - 40,
        duration: 300,
        useNativeDriver: false,
      }),
      Animated.timing(animatedOpacity, {
        toValue: 1,
        duration: 200,
        delay: 100,
        useNativeDriver: false,
      }),
    ]).start(() => {
      inputRef.current?.focus();
    });
  };

  const collapseAnimation = () => {
    Animated.parallel([
      Animated.timing(animatedWidth, {
        toValue: 50,
        duration: 300,
        useNativeDriver: false,
      }),
      Animated.timing(animatedOpacity, {
        toValue: 0,
        duration: 200,
        useNativeDriver: false,
      }),
    ]).start(() => {
      setIsExpanded(false);
    });
  };

  const handlePress = () => {
    if (!isExpanded) {
      expandAnimation();
    }
  };

  const handleBlur = () => {
    collapseAnimation();
  };

  return (
    <View style={styles.container}>
      <Text style={[styles.title, { color: theme.colors.onBackground }]}>History</Text>
      <Animated.View
        style={[
          styles.searchContainer,
          {
            width: animatedWidth,
            backgroundColor: theme.colors.elevation.level1,
          },
        ]}
      >
        <TouchableOpacity
          onPress={handlePress}
          style={styles.searchIcon}
        >
          <MaterialIcons
            name="search"
            size={24}
            color={theme.colors.onBackground}
          />
        </TouchableOpacity>
        <Animated.View
          style={[
            styles.inputContainer,
            {
              opacity: animatedOpacity,
            },
          ]}
        >
          <TextInput
            ref={inputRef}
            style={[styles.input, { color: theme.colors.onBackground }]}
            placeholder="Search history..."
            placeholderTextColor={theme.colors.onSurfaceVariant}
            onBlur={handleBlur}
          />
        </Animated.View>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  searchContainer: {
    height: 50,
    borderRadius: 25,
    flexDirection: 'row',
    alignItems: 'center',
    overflow: 'hidden',
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
  },
  searchIcon: {
    width: 50,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
  },
  inputContainer: {
    flex: 1,
    height: '100%',
    justifyContent: 'center',
  },
  input: {
    flex: 1,
    fontSize: 16,
    paddingRight: 15,
  },
});

export default History;
