import React, { useState, useEffect, useRef } from 'react';
import { StyleSheet, View, ScrollView, TouchableOpacity, Image, Animated, Dimensions } from 'react-native';
import { Text, Button, TextInput, Card, IconButton, Snackbar } from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
import { useNavigation, useFocusEffect } from '@react-navigation/native';
import { supabase } from '../lib/supabase';
import { NavigationProps } from '../navigation.types';
import { MaterialCommunityIcons } from '@expo/vector-icons';

// Import icons
const chatIcon = require('../assets/chat-icon.png');
const archiveIcon = require('../assets/archive-icon.png');

interface ChatItem {
  id: string;
  question: string;
  response: {
    verse: string;
    reference: string;
    relevance: string;
    explanation: string;
  };
  created_at: string;
  is_archived: boolean;
}

export default function Home() {
  const navigation = useNavigation<NavigationProps>();
  const [showHistory, setShowHistory] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [chats, setChats] = useState<ChatItem[]>([]);
  const [archivedChats, setArchivedChats] = useState<ChatItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [snackbarVisible, setSnackbarVisible] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');

  const searchWidth = useRef(new Animated.Value(40)).current;
  const pathOpacity = useRef(new Animated.Value(1)).current;
  const pathScale = useRef(new Animated.Value(1)).current;

  useEffect(() => {
    const animate = () => {
      Animated.sequence([
        Animated.parallel([
          Animated.timing(pathOpacity, {
            toValue: 0.5,
            duration: 1000,
            useNativeDriver: true,
          }),
          Animated.timing(pathScale, {
            toValue: 1.1,
            duration: 1000,
            useNativeDriver: true,
          }),
        ]),
        Animated.parallel([
          Animated.timing(pathOpacity, {
            toValue: 1,
            duration: 1000,
            useNativeDriver: true,
          }),
          Animated.timing(pathScale, {
            toValue: 1,
            duration: 1000,
            useNativeDriver: true,
          }),
        ]),
      ]).start(() => animate());
    };

    animate();
  }, []);

  const toggleSearch = () => {
    Animated.timing(searchWidth, {
      toValue: searchWidth._value === 40 ? 200 : 40,
      duration: 300,
      useNativeDriver: false,
    }).start();
  };

  // Load chats when the screen comes into focus
  useFocusEffect(
    React.useCallback(() => {
      loadChats();
    }, [])
  );

  const handleLogout = async () => {
    try {
      await supabase.auth.signOut();
      navigation.navigate('Login');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  const loadChats = async () => {
    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        console.error('No user found');
        navigation.navigate('Login');
        return;
      }

      console.log('Loading chats for user:', user.id);

      // First, check if the table exists and has the correct structure
      const { data: tableInfo, error: tableError } = await supabase
        .from('chats')
        .select('*')
        .limit(1);

      if (tableError) {
        console.error('Error checking table:', tableError);
        return;
      }

      console.log('Table structure:', tableInfo);

      // Now fetch the actual chats with all fields explicitly specified
      const { data: chatData, error } = await supabase
        .from('chats')
        .select(`
          id,
          user_id,
          question,
          response,
          created_at,
          is_archived
        `)
        .eq('user_id', user.id)
        .order('created_at', { ascending: false });

      if (error) {
        console.error('Error loading chats:', error);
        return;
      }

      console.log('Loaded chats:', chatData);

      if (!chatData) {
        console.log('No chat data returned');
        setChats([]);
        setArchivedChats([]);
        return;
      }

      // Parse the response field if it's stored as a string
      const processedChats = chatData.map(chat => ({
        ...chat,
        response: typeof chat.response === 'string' 
          ? JSON.parse(chat.response)
          : chat.response
      }));

      console.log('Processed chats:', processedChats);

      setChats(processedChats.filter(chat => !chat.is_archived));
      setArchivedChats(processedChats.filter(chat => chat.is_archived));
    } catch (error) {
      console.error('Error in loadChats:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleArchiveToggle = async (chatId: string, currentlyArchived: boolean) => {
    try {
      const { error } = await supabase
        .from('chats')
        .update({ is_archived: !currentlyArchived })
        .eq('id', chatId);

      if (error) {
        console.error('Error toggling archive status:', error);
        setSnackbarMessage('Failed to update archive status');
        setSnackbarVisible(true);
        return;
      }

      // Update local state immediately
      if (currentlyArchived) {
        // Moving from archived to unarchived
        const chatToUnarchive = archivedChats.find(chat => chat.id === chatId);
        if (chatToUnarchive) {
          setArchivedChats(prev => prev.filter(chat => chat.id !== chatId));
          setChats(prev => [{ ...chatToUnarchive, is_archived: false }, ...prev]);
          setSnackbarMessage('Chat unarchived successfully');
        }
      } else {
        // Moving from unarchived to archived
        const chatToArchive = chats.find(chat => chat.id === chatId);
        if (chatToArchive) {
          setChats(prev => prev.filter(chat => chat.id !== chatId));
          setArchivedChats(prev => [{ ...chatToArchive, is_archived: true }, ...prev]);
          setSnackbarMessage('Chat archived successfully');
        }
      }
      setSnackbarVisible(true);
    } catch (error) {
      console.error('Error in handleArchiveToggle:', error);
      setSnackbarMessage('An error occurred');
      setSnackbarVisible(true);
    }
  };

  const renderChatItem = (chat: ChatItem) => (
    <TouchableOpacity
      key={chat.id}
      onPress={() => navigation.navigate('Response', {
        question: chat.question,
        verse: chat.response.verse,
        reference: chat.response.reference,
        relevance: chat.response.relevance,
        explanation: chat.response.explanation
      })}
    >
      <Card style={styles.historyCard}>
        <View style={styles.historyCardHeader}>
          <Text style={styles.historyCardTitle}>{chat.question}</Text>
          <IconButton
            icon={chat.is_archived ? 'archive-remove' : 'archive'}
            size={24}
            onPress={() => handleArchiveToggle(chat.id, chat.is_archived)}
          />
        </View>
        <Text style={styles.historyCardContent}>
          {chat.response.verse} - {chat.response.reference}
        </Text>
        <Text style={styles.historyCardTime}>
          {new Date(chat.created_at).toLocaleDateString()}
        </Text>
      </Card>
    </TouchableOpacity>
  );

  const filteredChats = (showHistory ? chats : archivedChats).filter(chat =>
    chat.question.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.content}>
          <Image 
            source={require('../assets/logo-full.png')} 
            style={styles.logo} 
            resizeMode="contain"
          />

          <View style={styles.titleContainer}>
            <Text style={styles.mainTitle}>
              Seek Your Verse, Find Your{' '}
              <Animated.Text 
                style={[
                  styles.mainTitle, 
                  styles.highlight,
                  {
                    opacity: pathOpacity,
                    transform: [{ scale: pathScale }]
                  }
                ]}
              >
                Path
              </Animated.Text>
            </Text>
          </View>

          <TouchableOpacity 
            style={styles.newTopicButton} 
            onPress={() => navigation.navigate('Question')}
          >
            <Text style={styles.newTopicText}>+ New Topic</Text>
          </TouchableOpacity>

          <View style={styles.historySection}>
            <View style={styles.historyHeader}>
              <View style={styles.toggleContainer}>
                <TouchableOpacity
                  style={[
                    styles.toggleButton,
                    showHistory && styles.activeToggle
                  ]}
                  onPress={() => setShowHistory(true)}
                >
                  <Image source={chatIcon} style={styles.toggleIcon} />
                  <Text style={[
                    styles.toggleText,
                    showHistory && styles.activeToggleText
                  ]}>Chats</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={[
                    styles.toggleButton,
                    !showHistory && styles.activeToggle
                  ]}
                  onPress={() => setShowHistory(false)}
                >
                  <Image source={archiveIcon} style={styles.toggleIcon} />
                  <Text style={[
                    styles.toggleText,
                    !showHistory && styles.activeToggleText
                  ]}>Archive</Text>
                </TouchableOpacity>
              </View>
              <Animated.View style={[styles.searchContainer, {
                width: searchWidth,
                flexDirection: 'row',
                alignItems: 'center',
              }]}>
                <TouchableOpacity onPress={toggleSearch}>
                  <MaterialCommunityIcons name="magnify" size={24} color="#fff" />
                </TouchableOpacity>
                <Animated.View style={{ flex: 1, overflow: 'hidden', width: searchWidth }}>
                  <TextInput
                    style={styles.searchInput}
                    placeholder="Search history..."
                    value={searchQuery}
                    onChangeText={setSearchQuery}
                    placeholderTextColor="#666"
                  />
                </Animated.View>
              </Animated.View>
            </View>

            {loading ? (
              <Text style={styles.loadingText}>Loading your conversations...</Text>
            ) : filteredChats.length === 0 ? (
              <Text style={styles.emptyText}>
                {showHistory 
                  ? "No chat history yet. Start a new topic!" 
                  : "No archived chats yet."}
              </Text>
            ) : (
              <View style={styles.historyCards}>
                {filteredChats.map(renderChatItem)}
              </View>
            )}
          </View>

          <Button mode="outlined" onPress={handleLogout} style={styles.logoutButton}>
            Logout
          </Button>
          <Snackbar
            visible={snackbarVisible}
            onDismiss={() => setSnackbarVisible(false)}
            duration={2000}
          >
            {snackbarMessage}
          </Snackbar>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

