import React, { useState } from 'react';
import { StyleSheet, View, ScrollView, TouchableOpacity } from 'react-native';
import { Text, IconButton, Button, TextInput } from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
import { useNavigation } from '@react-navigation/native';
import { NavigationProps } from '../navigation.types';
import { BACKEND_URL } from '../config';
import { auth } from '../lib/firebase';

const exampleQuestions = [
  "I'm praying for healing for my mother who is battling cancer. Can you share a comforting verse and some guidance?",
  "I'm trying to decide whether to accept a new job offer or stay in my current position.",
  "I want to understand more about forgiveness and how to practice it",
  "I want to memorize Psalm 50:21-31. Can you help me with explanations and reflections on this verse?"
];

export default function Question() {
  const navigation = useNavigation<NavigationProps>();
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleExampleSelect = (selectedQuestion: string) => {
    setQuestion(selectedQuestion);
    handleSubmit(selectedQuestion);
  };

  const handleSubmit = async (questionText: string = question) => {
    if (!questionText.trim()) return;

    try {
      setLoading(true);
      setError(null);

      if (!auth.currentUser) {
        navigation.navigate('Login');
        return;
      }

      console.log('Making API request to:', `${BACKEND_URL}/generate`);
      const response = await fetch(`${BACKEND_URL}/generate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ prompt: questionText.trim() }),
      });

      console.log('Response status:', response.status);
      const responseData = await response.json();
      console.log('Response data:', responseData);

      if (!response.ok) {
        throw new Error(responseData.detail || 'Failed to get response');
      }

      // Navigate to Response screen with the verse data
      navigation.navigate('Response', {
        question: questionText.trim(),
        response: responseData
      });
    } catch (err: any) {
      console.error('API Error:', err);
      setError(err.message || 'Failed to get response. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <IconButton
          icon="arrow-left"
          iconColor="#FFD9D0"
          size={24}
          onPress={() => navigation.goBack()}
        />
        <Text style={styles.headerText}>Ask a Question</Text>
      </View>

      <ScrollView style={styles.content} contentContainerStyle={styles.contentContainer}>
        <Text style={styles.label}>Your Question</Text>
        <TextInput
          value={question}
          onChangeText={setQuestion}
          placeholder="Type your question here..."
          multiline
          numberOfLines={4}
          style={styles.input}
          mode="outlined"
        />

        {error ? <Text style={styles.errorText}>{error}</Text> : null}

        <Button
          mode="contained"
          onPress={() => handleSubmit()}
          style={styles.submitButton}
          loading={loading}
          disabled={loading || !question.trim()}
        >
          {loading ? 'Getting Response...' : 'Get Response'}
        </Button>

        <View style={styles.examplesSection}>
          <Text style={styles.examplesTitle}>Example Questions</Text>
          {exampleQuestions.map((q, index) => (
            <TouchableOpacity
              key={index}
              style={styles.exampleItem}
              onPress={() => handleExampleSelect(q)}
            >
              <Text style={styles.exampleText}>{q}</Text>
            </TouchableOpacity>
          ))}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 16,
  },
  headerText: {
    fontSize: 20,
    color: '#FFFFFF',
    marginLeft: 8,
    fontWeight: 'bold',
  },
  content: {
    flex: 1,
  },
  contentContainer: {
    padding: 16,
  },
  label: {
    fontSize: 16,
    color: '#FFFFFF',
    marginBottom: 8,
  },
  input: {
    backgroundColor: '#1A1A1A',
    marginBottom: 16,
  },
  errorText: {
    color: '#FF6B6B',
    marginBottom: 16,
    textAlign: 'center',
  },
  submitButton: {
    marginBottom: 24,
    backgroundColor: '#FFD9D0',
  },
  examplesSection: {
    marginTop: 16,
  },
  examplesTitle: {
    fontSize: 18,
    color: '#FFFFFF',
    marginBottom: 16,
    fontWeight: 'bold',
  },
  exampleItem: {
    backgroundColor: '#1A1A1A',
    padding: 16,
    borderRadius: 8,
    marginBottom: 12,
  },
  exampleText: {
    color: '#FFFFFF',
    fontSize: 14,
  },
});
